import React from 'react';

function Talk({ time, speaker, title, abstract,abstract1, bio }) {
  return (
    <div className="talk-container"> {/* Adds the container for layout and separation */}
      <div className="talk-details">
        <div className="talk-time">
          <p>{time} |</p> {/* Keeps the time next to the divider as per the original */}
        </div>
        <div className="talk-abstract">
          <h3>{speaker}</h3> {/* Speaker name at the top as in the original */}
          <p><strong>Title:</strong> <span className="normal-text">{title}</span></p>
          <p><strong>Abstract:</strong> {abstract}{abstract1}</p>
          {bio && <p><strong>Bio:</strong> {bio}</p>} {/* Conditional rendering of the bio */}
        </div>
      </div>
    </div>
  );
}

export default Talk;
